mapper.select:
WITH recursive classifyCategory AS (
SELECT categoryName, cno, categoryLevel, 1 AS level FROM categorydto WHERE categoryLevel IS null UNION all SELECT ano.categoryName, ano.cno, ano.categoryLevel, 1 + LEVEL AS level FROM categorydto as ano INNER JOIN classifyCategory ON ano.categoryLevel = classifyCategory.cno
) SELECT categoryName, cno, categoryLevel, level FROM classifyCategory;
